mapper.select:
 select CDS_MESSAGE_LABEL_INFO.* ,ABD_AREA_NAME from CDS_MESSAGE_LABEL_INFO left join CDS_AREA_BASE_DTLS on CDS_MESSAGE_LABEL_INFO. MLI_AREA_ID =CDS_AREA_BASE_DTLS.ABD_AREA_ID


     and MLI_AREA_ID = #{mliAreaId,jdbcType=VARCHAR}


     and  MLI_LABEL_CODE like concat('%',#{mliLabelCode}, '%')


     and  MLI_CUS_NUMBER = #{mliCusNumber,jdbcType=VARCHAR}


     and  MLI_LABEL_TYPE = #{mliLabelType,jdbcType=INTEGER}


order by MLI_AREA_ID

select *
from CDS_MESSAGE_LABEL_INFO


    and  MLI_LABEL_CODE = #{mliLabelCode}


    and  MLI_CUS_NUMBER = #{mliCusNumber,jdbcType=VARCHAR}


    and  MLI_LABEL_TYPE = #{mliLabelType,jdbcType=INTEGER}
