mapper.select:
select SPR_PEOPLE_ID as "id",SPR_PEOPLE as "name" from CDS_SPORADIC_FLOW_PEOPLE_REGISTER
AND SPR_REGISTER_ID = #{id}
AND SPR_PEOPLE_TYPE = '1'

select SPR_PEOPLE_ID as "PRSNR_IDNTY",SPR_PEOPLE as "NAME",SPR_JSH as "JSH" from CDS_SPORADIC_FLOW_PEOPLE_REGISTER
AND SPR_REGISTER_ID = #{id}
AND SPR_PEOPLE_TYPE = '2'
